settings.custom_styles:
.bf-sale-banner .img-banner-block-content span.h6,
  .bf-sale-banner .img-banner-block-content h1{
    position: relative;
  }
  .bf-sale-banner .img-banner-block-content span.h6 {
    width: fit-content;
    overflow: visible;
    min-width: 100%;
    margin-bottom: 20px;
  }
  .bf-sale-banner .img-banner-block-content span.h6:before {
    content: " ";
    position: absolute;
    background: url("https://cdn.shopify.com/s/files/1/0220/4008/4552/files/Circle_V1.svg") no-repeat;
    width: 188px;
    height: 41px;
    left: -15px;
    top: 0px;
    z-index: -1;
    background-size: 100%;
  }
  .bf-sale-banner .img-banner-block-content h1 {
    color: #E2F03C;
    padding-left: 20px;
    display: flex;
    align-items: center;
  }
  .bf-sale-banner .img-banner-block-content h1:before {
    content: "UP TO";
    position: absolute;
    font-size: 23px;
    font-family: var(--font-juniper) !important;
    transform: rotate(270deg);
    left: -30px;
  }
  .bf-sale-banner .img-banner-block-content {
    flex-direction: row !important;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: center;
  }

  @media screen and ( max-width: 639.9px ) {
    .bf-sale-banner .img-banner-block-content span.h6 {
      min-width: fit-content;
      left: 24px;
    }
  }
  @media screen and ( max-width: 640px ) {
    .bf-sale-banner .img-banner-block-content span.h6 {
      font-size: 14px;
      margin-bottom: 6px;
    }
    .bf-sale-banner .img-banner-block-content span.h6:before {
      background: url("https://cdn.shopify.com/s/files/1/0220/4008/4552/files/Line_V1.svg?v=1700681705") no-repeat;
      left: 0px;
      top: 18px;
    }
    .bf-sale-banner .img-banner-block-content h1 {
    font-size: 10vw;
    line-height: 40px;
    letter-spacing: 2px;
      padding-left: 30px;
      padding-bottom: .5rem;
    }

    .bf-sale-banner .img-banner-block-content h1:before {
      font-size: 11px;
      left: 5px;
    }

  }
